mapper.delete:
DELETE FROM ISSUE
WHERE
	PRJCT_SN = #{prjct_sn}
	AND ISSUE_SN = #{issue_sn}

mapper.update:
UPDATE ISSUE
SET ISSUE_STTUS_CODE = 0
WHERE 
	PRJCT_SN = #{prjct_sn}
	AND ISSUE_SN = #{issue_sn}

UPDATE ISSUE
SET ISSUE_STTUS_CODE = 1
WHERE 
	PRJCT_SN = #{prjct_sn}
	AND ISSUE_SN = #{issue_sn}
