mapper.select:
select a.mber_ntcn_time, a.mber_schdul_sn , b.mber_schdul_nm, a.mber_ntcn_sn from mem_schedule_notification a, member_schedule b , member c where a.mber_schdul_sn = b.mber_schdul_sn and b.mber_sn = c.mber_sn and c.mber_sn = #{mber_sn}
and a.mber_ntcn_time < to_char(sysdate+1/24, 'yyyy-mm-dd hh24:mi') and a.mber_ntcn_time > to_char(sysdate,'yyyy-mm-dd hh24:mi')
and a.ntcn_cnfirm_at_code = 0

select count(*) from mem_schedule_notification a, member_schedule b , member c where a.mber_schdul_sn = b.mber_schdul_sn and b.mber_sn = c.mber_sn and c.mber_sn = #{mber_sn}
and a.mber_ntcn_time < to_char(sysdate+1/24, 'yyyy-mm-dd hh24:mi') and a.mber_ntcn_time > to_char(sysdate,'yyyy-mm-dd hh24:mi')
and a.ntcn_cnfirm_at_code = 0
